  /**
   * Constructs a new instanced buffer geometry.
   */
  constructor() {
    super();

    /**
     * This flag can be used for type testing.
     *
     * @type {boolean}
     * @readonly
     * @default true
     */
    this.isInstancedBufferGeometry = true;
    this.type = 'InstancedBufferGeometry';

