import React, { useState, useEffect, useRef } from 'react';
import { useParams, Link } from 'react-router-dom';
import io from 'socket.io-client';
import Editor from '@monaco-editor/react';
import { motion } from 'framer-motion';

const socket = io('http://localhost:5000');

function CodeEditor() {
  const { roomId } = useParams();
  const [html, setHtml] = useState('');
  const [css, setCss] = useState('');
  const [js, setJs] = useState('');
  const [language, setLanguage] = useState('javascript');
  const [users, setUsers] = useState(1);
  const [connected, setConnected] = useState(false);
  const [messages, setMessages] = useState([]);
  const [messageText, setMessageText] = useState('');
  const [isChatOpen, setIsChatOpen] = useState(true);
  const [typingUsers, setTypingUsers] = useState([]);
  const [participants, setParticipants] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [messageReactions, setMessageReactions] = useState({});
  const previewRef = useRef(null);
  const messagesEndRef = useRef(null);
  const typingTimeoutRef = useRef(null);
  const notificationTimeouts = useRef([]);

  useEffect(() => {
    socket.emit('join-room', roomId, socket.id);

    socket.on('room-joined', (data) => {
      setHtml(data.html || '');
      setCss(data.css || '');
      setJs(data.js || '');
      setMessages(data.messages || []);
      setUsers(data.users || 1);
      setParticipants(data.participants || [socket.id]); // Assume backend sends participants array
      setConnected(true);
      addNotification('success', `Joined room ${roomId}`);
    });

    socket.on('user-joined', (userId) => {
      setUsers(prev => prev + 1);
      setParticipants(prev => [...prev, userId]);
      addNotification('success', `${userId.substring(0,6)} joined the room`);
    });

    socket.on('user-left', (userId) => {
      setUsers(prev => prev - 1);
      setParticipants(prev => prev.filter(id => id !== userId));
      addNotification('error', `${userId.substring(0,6)} left the room`);
    });

    socket.on('user-count', (count) => {
      setUsers(count);
    });

    socket.on('code-update', (data) => {
      setHtml(data.html);
      setCss(data.css);
      setJs(data.js);
      addNotification('info', 'Code updated by another user');
    });

    socket.on('new-message', (message) => {
      setMessages(prev => [...prev, message]);
    });

    socket.on('user-typing', (data) => {
      const { userId, isTyping } = data;
      setTypingUsers(prev => {
        if (isTyping) {
          return [...prev.filter(u => u !== userId), userId];
        } else {
          return prev.filter(u => u !== userId);
        }
      });
    });

    return () => {
      socket.off('room-joined');
      socket.off('user-joined');
      socket.off('user-left');
      socket.off('user-count');
      socket.off('code-update');
      socket.off('new-message');
      socket.off('user-typing');
    };
  }, [roomId]);

  const handleCodeChange = (type, value) => {
    if (type === 'html') setHtml(value);
    if (type === 'css') setCss(value);
    if (type === 'js') setJs(value);

    socket.emit('code-change', { roomId, html, css, js });
  };

  const copyLink = () => {
    navigator.clipboard.writeText(`${window.location.origin}/room/${roomId}`);
    alert('Link copied!');
  };

  const sendMessage = () => {
    if (!messageText.trim()) return;
    const message = {
      userId: socket.id.substring(0, 6), // Temp user name
      message: messageText,
      timestamp: Date.now()
    };
    socket.emit('send-message', { roomId, ...message });
    socket.emit('typing', { roomId, userId: socket.id, isTyping: false });
    setMessageText('');
  };

  const handleInputChange = (e) => {
    setMessageText(e.target.value);
    socket.emit('typing', { roomId, userId: socket.id, isTyping: true });
    clearTimeout(typingTimeoutRef.current);
    typingTimeoutRef.current = setTimeout(() => {
      socket.emit('typing', { roomId, userId: socket.id, isTyping: false });
    }, 1000);
  };

  const addEmoji = (emoji) => {
    setMessageText(prev => prev + emoji);
  };

  const toggleChat = () => {
    setIsChatOpen(prev => !prev);
  };

  const addNotification = (type, message) => {
    const id = Date.now();
    setNotifications(prev => [...prev, { id, type, message, timestamp: Date.now() }]);
    const timeout = setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== id));
    }, 5000);
    notificationTimeouts.current.push(timeout);
  };

  useEffect(() => {
    return () => {
      notificationTimeouts.current.forEach(clearTimeout);
    };
  }, []);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    const preview = previewRef.current;
    if (preview) {
      preview.srcdoc = `
        <!DOCTYPE html>
        <html>
          <head>
            <style>${css}</style>
          </head>
          <body>${html}
            <script>${js}</script>
          </body>
        </html>
      `;
    }
  }, [html, css, js]);

  const formatMessage = (msg) => {
    const time = new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    return (
      <motion.div
        key={msg.timestamp}
        className="message"
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.3 }}
      >
        <span className="user-name">{msg.userId}:</span>
        <span className="message-text">{msg.message}</span>
        <span className="message-time">{time}</span>
      </motion.div>
    );
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.5,
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { duration: 0.4 }
    }
  };

  const chatVariants = {
    open: { width: '300px', opacity: 1 },
    closed: { width: 0, opacity: 0, transition: { duration: 0.3 } }
  };

  return (
    <motion.div
      className="editor-container"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      <div className="notifications">
        {notifications.map(notification => (
          <motion.div
            key={notification.id}
            className={`notification ${notification.type}`}
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.3 }}
          >
            {notification.message}
          </motion.div>
        ))}
      </div>

      <motion.header
        className="editor-header"
        variants={itemVariants}
        whileHover={{ scale: 1.02 }}
      >
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <Link to="/" className="btn" style={{ padding: '0.5rem 1rem', fontSize: '0.9rem' }}>← Back</Link>
          <h2>Room: {roomId}</h2>
        </div>
        <div className="status">
          <motion.span
            className={`connection ${connected ? 'connected' : 'disconnected'}`}
            animate={{ scale: connected ? 1 : [1, 1.1, 1] }}
            transition={{ duration: 1, repeat: Infinity }}
          >
            {connected ? 'Connected' : 'Disconnected'}
          </motion.span>
          <span>Users: {users}</span>
          <button onClick={copyLink} className="btn">Copy Link</button>
        </div>
      </motion.header>

      <motion.div 
        className="main-content"
        variants={itemVariants}
      >
        <motion.div className="editors">
          <motion.div className="editor-pane" variants={itemVariants}>
            <label>HTML</label>
            <Editor
              height="100%"
              language="html"
              value={html}
              onChange={(value) => handleCodeChange('html', value)}
              theme="vs-dark"
              options={{ minimap: { enabled: false } }}
            />
          </motion.div>

          <motion.div className="editor-pane" variants={itemVariants}>
            <label>CSS</label>
            <Editor
              height="100%"
              language="css"
              value={css}
              onChange={(value) => handleCodeChange('css', value)}
              theme="vs-dark"
              options={{ minimap: { enabled: false } }}
            />
          </motion.div>

          <motion.div className="editor-pane" variants={itemVariants}>
            <label>JavaScript</label>
            <select value={language} onChange={(e) => setLanguage(e.target.value)}>
              <option value="javascript">JavaScript</option>
              <option value="python">Python</option>
              <option value="java">Java</option>
              {/* Add more languages */}
            </select>
            <Editor
              height="100%"
              language={language}
              value={js}
              onChange={(value) => handleCodeChange('js', value)}
              theme="vs-dark"
              options={{ minimap: { enabled: false } }}
            />
          </motion.div>
        </motion.div>

        <motion.div className="preview-pane" variants={itemVariants}>
          <label>Live Preview</label>
          <iframe ref={previewRef} title="preview" className="preview" />
        </motion.div>
      </motion.div>

      <motion.div 
        className={`chat-pane ${isChatOpen ? 'open' : 'closed'}`}
        variants={chatVariants}
        initial={false}
        animate={isChatOpen ? "open" : "closed"}
      >
        <div className="chat-header">
          <h3>Chat ({users} online)</h3>
          <button onClick={toggleChat} className="toggle-chat-btn">
            {isChatOpen ? '−' : '+'}
          </button>
        </div>
        <div className="chat-messages">
          {messages.map(formatMessage)}
          {typingUsers.length > 0 && (
            <div className="typing-indicator">
              {typingUsers.map(user => user.substring(0, 6)).join(', ')} is typing...
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
        <div className="chat-input-container">
          <input
            type="text"
            value={messageText}
            onChange={handleInputChange}
            placeholder="Type a message..."
            onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
          />
          <div className="emoji-picker">
            <button onClick={() => addEmoji('😊')}>😊</button>
            <button onClick={() => addEmoji('👍')}>👍</button>
            <button onClick={() => addEmoji('🚀')}>🚀</button>
            <button onClick={() => addEmoji('💡')}>💡</button>
          </div>
          <button onClick={sendMessage} className="send-btn">Send</button>
        </div>
      </motion.div>
    </motion.div>
  );
}

export default CodeEditor;
