import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';
import Editor from '@monaco-editor/react';
import { motion } from 'framer-motion';

const socket = io('http://localhost:5000');

function CodeEditor() {
  const { roomId } = useParams();
  const [html, setHtml] = useState('');
  const [css, setCss] = useState('');
  const [js, setJs] = useState('');
  const [language, setLanguage] = useState('javascript');
  const [users, setUsers] = useState(1);
  const [connected, setConnected] = useState(false);
  const [messages, setMessages] = useState([]);
  const [messageText, setMessageText] = useState('');
  const [isChatOpen, setIsChatOpen] = useState(true);
  const [typingUsers, setTypingUsers] = useState([]);
  const previewRef = useRef(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket.emit('join-room', roomId, socket.id);

    socket.on('room-joined', (data) => {
      setHtml(data.html || '');
      setCss(data.css || '');
      setJs(data.js || '');
      setMessages(data.messages || []);
      setUsers(data.users || 1);
      setConnected(true);
    });

    socket.on('user-joined', () => {
      setUsers(prev => prev + 1);
    });

    socket.on('user-count', (count) => {
      setUsers(count);
    });

    socket.on('code-update', (data) => {
      setHtml(data.html);
      setCss(data.css);
      setJs(data.js);
    });

    socket.on('new-message', (message) => {
      setMessages(prev => [...prev, message]);
    });

    socket.on('user-typing', (data) => {
      const { userId, isTyping } = data;
      setTypingUsers(prev => {
        if (isTyping) {
          return [...prev.filter(u => u !== userId), userId];
        } else {
          return prev.filter(u => u !== userId);
        }
      });
    });

    return () => {
      socket.off('room-joined');
      socket.off('user-joined');
      socket.off('user-count');
      socket.off('code-update');
      socket.off('new-message');
      socket.off('user-typing');
    };
  }, [roomId]);

  const handleCodeChange = (type, value) => {
    if (type === 'html') setHtml(value);
    if (type === 'css') setCss(value);
    if (type === 'js') setJs(value);

    socket.emit('code-change', { roomId, html, css, js });
  };

  const copyLink = () => {
    navigator.clipboard.writeText(`${window.location.origin}/room/${roomId}`);
    alert('Link copied!');
  };

  const sendMessage = () => {
    if (!messageText.trim()) return;
    const message = {
      userId: socket.id.substring(0, 6), // Temp user name
      message: messageText,
      timestamp: Date.now()
    };
    socket.emit('send-message', { roomId, ...message });
    setMessageText('');
  };

  const addEmoji = (emoji) => {
    setMessageText(prev => prev + emoji);
  };

  const toggleChat = () => {
    setIsChatOpen(prev => !prev);
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    const preview = previewRef.current;
    if (preview) {
      preview.srcdoc = `
        <!DOCTYPE html>
        <html>
          <head>
            <style>${css}</style>
          </head>
          <body>${html}
            <script>${js}</script>
          </body>
        </html>
      `;
    }
  }, [html, css, js]);

  const formatMessage = (msg) => {
    const time = new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    return (
      <div key={msg.timestamp} className="message">
        <span className="user-name">{msg.userId}:</span>
        <span className="message-text">{msg.message}</span>
        <span className="message-time">{time}</span>
      </div>
    );
  };

  return (
    <motion.div
      className="editor-container"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <header className="editor-header">
        <h2>Room: {roomId}</h2>
        <div className="status">
          <span className={`connection ${connected ? 'connected' : 'disconnected'}`}>
            {connected ? 'Connected' : 'Disconnected'}
          </span>
          <span>Users: {users}</span>
          <button onClick={copyLink} className="btn">Copy Link</button>
        </div>
      </header>

      <div className="main-content">
        <div className="editors" style={{ display: 'flex', flexDirection: 'column', gap: '20px', flex: 1 }}>
          <div className="editor-pane" style={{ flex: 1 }}>
            <label>HTML</label>
            <Editor
              height="300px"
              language="html"
              value={html}
              onChange={(value) => handleCodeChange('html', value)}
              theme="vs-dark"
              options={{ minimap: { enabled: false } }}
            />
          </div>

          <div className="editor-pane" style={{ flex: 1 }}>
            <label>CSS</label>
            <Editor
              height="300px"
              language="css"
              value={css}
              onChange={(value) => handleCodeChange('css', value)}
              theme="vs-dark"
              options={{ minimap: { enabled: false } }}
            />
          </div>

          <div className="editor-pane" style={{ flex: 1 }}>
            <label>JavaScript</label>
            <select value={language} onChange={(e) => setLanguage(e.target.value)}>
              <option value="javascript">JavaScript</option>
              <option value="python">Python</option>
              <option value="java">Java</option>
              {/* Add more languages */}
            </select>
            <Editor
              height="300px"
              language={language}
              value={js}
              onChange={(value) => handleCodeChange('js', value)}
              theme="vs-dark"
              options={{ minimap: { enabled: false } }}
            />
          </div>
        </div>

        <div className="preview-pane" style={{ flex: 0.5 }}>
          <label>Live Preview</label>
          <iframe ref={previewRef} title="preview" className="preview" />
        </div>
      </div>

      <div className={`chat-pane ${isChatOpen ? 'open' : 'closed'}`}>
        <div className="chat-header">
          <h3>Chat ({users} online)</h3>
          <button onClick={toggleChat} className="toggle-chat-btn">
            {isChatOpen ? '−' : '+'}
          </button>
        </div>
        <div className="chat-messages">
          {messages.map(formatMessage)}
          <div ref={messagesEndRef} />
        </div>
        <div className="chat-input-container">
          <input
            type="text"
            value={messageText}
            onChange={(e) => setMessageText(e.target.value)}
            placeholder="Type a message..."
            onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
          />
          <div className="emoji-picker">
            <button onClick={() => addEmoji('😊')}>😊</button>
            <button onClick={() => addEmoji('👍')}>👍</button>
            <button onClick={() => addEmoji('🚀')}>🚀</button>
            <button onClick={() => addEmoji('💡')}>💡</button>
          </div>
          <button onClick={sendMessage} className="send-btn">Send</button>
        </div>
      </div>
    </motion.div>
  );
}

export default CodeEditor;
